mapper.select:
SELECT pk_product_no, fk_member_no, fk_region_no, fk_category_no, fk_category_detail_no, product_title, product_contents, product_price, product_status, product_sale_status, product_regdate, product_update_date, product_sale_type, product_views, pk_prod_img_no, prod_img_name, prod_img_org_name, prod_img_is_thumbnail, pk_region_no, region_state, region_city, region_town, region_lat, region_lng FROM( SELECT rownum AS rnum, p.* FROM( select P.pk_product_no, P.fk_member_no, P.fk_region_no, P.fk_category_no, P.fk_category_detail_no, P.product_title, P.product_contents, P.product_price, P.product_status, P.product_sale_status, P.product_regdate, P.product_update_date, P.product_sale_type, P.product_views, I.pk_prod_img_no, I.prod_img_name, I.prod_img_org_name, I.prod_img_is_thumbnail, R.pk_region_no, R.region_state, R.region_city, R.region_town, R.region_lat, R.region_lng from tbl_product P JOIN tbl_prod_img I ON P.pk_product_no = I.fk_product_no JOIN tbl_region R ON P.fk_region_no = R.pk_Region_No where I.prod_img_is_thumbnail = 1 and P.product_sale_status IN (0, 1)
and lower(P.product_title) like '%'||lower(#{search_prod})||'%'
and P.fk_category_no = #{category_no}
and P.fk_category_detail_no = #{category_detail_no}
and P.product_price >= to_number(#{min_price})
and P.product_price <= to_number(#{max_price})
and P.fk_region_no = to_number(#{region}) and R.region_town = #{town}
order by P.product_update_date desc order by P.product_price desc order by P.product_price asc order by P.product_update_date desc
) P ) WHERE rnum BETWEEN #{paging_dto.first_row} AND #{paging_dto.last_row}

select pk_prod_img_no, fk_product_no, prod_img_name, prod_img_org_name, prod_img_is_thumbnail
from tbl_prod_img
where fk_product_no = #{pk_product_no}

mapper.update:
update tbl_product
set product_views = product_views + 1
where pk_product_no = #{pk_product_no}

update tbl_product 
set product_update_date = SYSDATE  
where pk_product_no = #{pk_product_no}

update tbl_product 
set product_sale_status = #{sale_status_no} 
where pk_product_no = #{pk_product_no}

mapper.select:
select p.pk_product_no, p.product_title, p.product_price, pi.prod_img_name from tbl_product p join tbl_prod_img pi on p.pk_product_no = pi.fk_product_no where pi.prod_img_is_thumbnail = 1 and p.pk_product_no in
#{pk_product_no}
order by pk_product_no
